body{
    background-color: lightblue;
}
.chatbox{
    position: fixed;
    right: 3%;
    bottom: 10px;
    height: 350px;
    width: 250px;
    display: flex;
    flex-direction: column;
    overflow: hidden;
    box-shadow: 2px 0px 5px grey;
    transition: all 0.5s ease;
    .chatbox-header{
        padding: 5px;
        background-color: royalblue;
        color: white;
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        .chatbox-title{
            width: 20px;
            height: 20px;
            padding: 5px;
            text-align: center;
            svg{
                color: white;
            }
        }
        .chatbox-actions{
            display: flex;
            justify-content: center;
            align-items: center;
            .chatbox-action{
                margin-right: 0px;
                width: 20px;
                height: 20px;
                cursor: pointer;
                padding: 5px;
                text-align: center;
                svg{
                    width: 18px;
                    height: 18px;
                    color: white;
                }
                &:hover{
                    svg{
                        width: 20px;
                        height: 20px;
                    }
                }
            }
        }
    }
    .chatbox-log{
        height: 250px;
        padding: 5px;
        display: flex;
        overflow-y: scroll;
        flex-direction: column;
        background-color: lightcyan;
        .chat-msg-row{
            margin-bottom: 5px;
            .chat-bubble{
                width: 150px;
                color: whitesmoke;
                padding: 5px;
                border-radius: 4px;
                box-shadow: 2px 0px 5px grey;
            }
            .chat-bubble-right{
                background-color: darkslateblue;
                margin-right: 2px;
                margin-left: auto;
            }
            .chat-bubble-left{
                background-color: forestgreen;
                margin-left: 2px;
                margin-right: auto;
            }
        }
    }
    .chatbox-msg{
        height: 60px;
        display: flex;
        .chat-msg-box{
            width: 190px;
            height: 60px;
            textarea#chat-msg{
                width: 185px;
                height: 56px;
                resize: none;
                border: none;
                border-right: 1px solid lightblue;
            }
        }
        .chat-send-button{
            cursor: pointer;
            background-color: white;
            display: flex;
            align-items: center;
            justify-content: space-between;
            &:hover{
                svg{
                    padding: 10px;
                    width: 40px;
                    height: 40px;
                }
            }
            svg{
                padding: 15px;
                width: 30px;
                height: 30px;
                color: royalblue;
            }
        }
    }
}
.hidden{
    display: none;
}
.minimize{
    height: 40px;
    bottom: 10px;
    box-shadow: 0px 0px 0px grey;
    transition: all 0.5s ease;
    overflow: hidden;
    .chatbox-log{
        transition: all 0.5s ease;
        overflow: hidden;
        height: 0px;
    }
    .chatbox-msg{
        transition: all 0.5s ease;
        overflow: hidden;
        height: 0px;
    }
}
.maximize{
    height: 350px;
    bottom: 10px;
    box-shadow: 2px 0px 5px grey;
    transition: all 0.5s ease;
    .chatbox-log{
        transition: all 0.5s ease;
        height: 250px;
    }
    .chatbox-msg{
        transition: all 0.5s ease;
        height: 60px;
    }
}
