.chatbox{
    position: fixed;
    right: 20px;
    bottom: 10px;
    height: 350px;
    width: 250px;
    display: flex;
    flex-direction: column;
    box-shadow: 2px 0px 5px grey;
    .chatbox-header{
        padding: 5px;
        background-color: blue;
        color: white;
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        .chatbox-title{
            margin-top: auto;
            margin-bottom: auto;
            margin-left: 10px;
            font-size: 1.2em;
        }
        .chatbox-actions{
            display: flex;
            justify-content: center;
            align-items: center;
            .chatbox-action{
                margin-right: 0px;
                width: 20px;
                height: 20px;
                cursor: pointer;
                padding: 5px;
                text-align: center;
                svg{
                    width: 18px;
                    height: 18px;
                    color: white;
                }
                &:hover{
                    svg{
                        width: 20px;
                        height: 20px;
                    }
                }
            }
        }
    }
    .chatbox-log{
        height: 250px;
        padding: 5px;
        display: flex;
        flex-direction: column;
        background-color: lightblue;
    }
    .chatbox-msg{
        height: 60px;
        display: flex;
        .chat-msg-box{
            width: 190px;
            height: 60px;
            textarea#chat-msg{
                width: 185px;
                height: 55px;
                resize: none;
                border: none;
                border-right: 1px solid lightblue;
            }
        }
        .chat-send-button{
            width: 60px;
            height: 60px;
            padding: 10px;
            cursor: pointer;
            text-align: center;
            &:hover{
                padding: 8px;
            }
            svg{
                color: lightskyblue;
            }
        }
    }
}
.hide{
    display: none;
}
.show{
    display: flex;
}
.minimize{
    height: 50px;
    bottom: 0px;
    box-shadow: 0px 0px 0px grey;
    .chatbox-log{
        display: none;
    }
    .chatbox-msg{
        display: none;
    }
}
.maximize{
    height: 350px;
    bottom: 10px;
    box-shadow: 2px 0px 5px grey;
    .chatbox-log{
        display: flex;
    }
    .chatbox-msg{
        display: flex;
    }
}
